import React from 'react';
import { CardTitle, Row, Col, CardImg } from 'reactstrap';
import './movieList.scss';
import Carousel from 'react-elastic-carousel';


const MovieList = props => {
    return (
        <Row className="section-movieList">
            <Col xs="12">
                <CardTitle>{props.title}</CardTitle>
            </Col>
            <Carousel itemsToShow={6}>
                {
                    props.listMovie?.map((item, index) => (

                        <Col key={index} className="box-image" xs="12" lg="12">
                            <CardImg style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original${item[0].poster_path})` }}>
                            </CardImg>
                        </Col>

                    ))
                }
            </Carousel>
        </Row >
    );
}

export default MovieList;
