import React from 'react';
import { Card } from 'reactstrap'
import Banner from '../component/banner';
import DetailMovie from '../component/detailMovie';
import MovieList from '../component/movieList';

const Browse = props => {
	const [status, setstatus] = React.useState(false);
	const handleClick = (id, status) => {
		props.handleDetail(id);
		setstatus(status);
		console.log(status);
	};
	return (
		<Card className="page">
			<Banner
				poster_path={props.poster_path}
				name={props.name}
				overview={props.overview}>
			</Banner>
			<MovieList
				listMovie={props.Original}
			/>
			<MovieList
				title="Xếp hạng cao"
				listMovie={props.Toprated}
				handlePassData={handleClick} />
			<MovieList
				title="Hành động"
				listMovie={props.Action}
				handlePassData={handleClick()} />
			/>
			<MovieList
				title="Hài"
				listMovie={props.Comedy}
				handlePassData={handleClick} />
			<MovieList
				title="Kinh dị"
				listMovie={props.Horror}
				handlePassData={handleClick} />
			<MovieList
				title="Lãng mạn"
				listMovie={props.Romance}
				handlePassData={handleClick} />
			<MovieList
				title="Tài Liệu"
				listMovie={props.Document}
				handlePassData={handleClick} />
			<DetailMovie
				title={props.titleTrailer}
				date_release={props.date_release}
				vote={props.vote}
				overview={props.overview}
				url={props.url}
				status={status} />
		</Card>
	);
}

export default Browse;
