import React from 'react';
import { CardTitle, Row, Col, CardImg, Button } from 'reactstrap';
import './movieList.scss';
import Slider from "react-slick";

const setting = {
    dots: false,
    infinity: true,
    speed: 500,
    slidesToShow: 8,
    slidesToScroll: 1
}

const MovieList = (props) => {
    const handleClick = (item) => {
        props.handlePassData(item)
    }
    const handleOutside = (e) => {
        if (e.target.classList.contains('box-item-movie') != 'true' || e.target.classList.contains('detail-movie') != 'true') {
            // props.handleClickOutside('false');
            console.log(e.target.classList.contains('box-item-movie'));
        }
    }
    return (
        <Row className="section-movieList">
            <Col xs="12">
                <CardTitle className="h2 title">{props.title}</CardTitle>
            </Col>
            <Slider {...setting}>
                {
                    props.listMovie?.map((item, index) => (
                        <Col key={index} className="box-image" lg="1" >
                            <Button onClick={(e) => (handleClick({ ...item[0], status: true }))}
                                style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original${item[0].poster_path})` }}
                                className="box-item-movie">
                            </Button>
                        </Col>
                    ))
                }
            </Slider>
        </Row >
    );
}

export default MovieList;
