import React from 'react';
import { Card } from 'reactstrap'
import Banner from '../component/banner';
import DetailMovie from '../component/detailMovie';
import MovieList from '../component/movieList';

const Browse = props => {
	const [status, setstatus] = React.useState('false');
	const [detailMovie, setdetailMovie] = React.useState(null);
	function handleClick(item) {
		setdetailMovie(item);
		setstatus(item.status);
		props.handleIdvideo(item.id);
	};
	document.addEventListener('click', (e) => {
		if (!e.target.classList.contains('box-image') && !e.target.classList.contains('detail-movie')) {
			setstatus('false');
		} else if (e.target.classList.contains('box-image') || e.target.classList.contains('detail-movie')) {
			setstatus('true');
		}
		console.log(status)
	})
	return (
		<Card className="page">
			<Banner
				poster_path={props.poster_path}
				name={props.name}
				overview={props.overview}>
			</Banner>
			<MovieList
				listMovie={props.Original}
				handlePassData={handleClick}
			/>
			<MovieList
				title="Xếp hạng cao"
				listMovie={props.Toprated}
				handlePassData={handleClick}
			/>
			<MovieList
				title="Hành động"
				listMovie={props.Action}
				handlePassData={handleClick}
			/>
			<MovieList
				title="Hài"
				listMovie={props.Comedy}
				handlePassData={handleClick}
			/>
			<MovieList
				title="Kinh dị"
				listMovie={props.Horror}
				handlePassData={handleClick}
			/>
			<MovieList
				title="Lãng mạn"
				listMovie={props.Romance}
				handlePassData={handleClick}
			/>
			<MovieList
				title="Tài Liệu"
				listMovie={props.Document}
				handlePassData={handleClick}
			/>
			<DetailMovie
				title={detailMovie?.name}
				date_release={detailMovie?.first_air_date}
				vote={detailMovie?.vote_average}
				overview={detailMovie?.overview}
				url={detailMovie?.id}
				status={status} />
		</Card>
	);
}

export default Browse;
