import React from 'react';
import { CardTitle, Row, Col, CardImg } from 'reactstrap'

const MovieList = props => {
    const list = [];
    const updateList = () => {
        if (props.listMovie != null) {
            for (let i = 0; i = props.listMovie.length; i++) {
                list.push(props.listMovie[i].poster_path)()
            }
        }
    }

    // for (let i = 0; i < list.length; i++) {

    // }
    console.log(list);
    return (
        <Row>
            <Col xs="12">
                <CardTitle>{props.title}</CardTitle>
            </Col>
            {
                props.listMovie?.map((item, index) => (
                    <Col key={index}>
                        <CardImg style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original/${item.poster_path})` }}></CardImg>
                    </Col>
                ))
            }
        </Row>
    );
}

export default MovieList;
