import React from 'react';
import { Card } from 'reactstrap'
import Banner from '../component/banner';
import DetailMovie from '../component/detailMovie';
import MovieList from '../component/movieList';

const Browse = props => {
	const [status, setstatus] = React.useState(false);
	const handleClick = React.useCallback((id, name, overview, vote_average, first_air_date, status) => {
		// console.log(item.id, item.name, item.overview, item.vote_average, item.first_air_date, item.status);
		console.log(id, name, overview, vote_average, first_air_date, status);
	}, []);
	return (
		<Card className="page">
			<Banner
				poster_path={props.poster_path}
				name={props.name}
				overview={props.overview}>
			</Banner>
			<MovieList
				listMovie={props.Original}
			/>
			<MovieList
				title="Xếp hạng cao"
				listMovie={props.Toprated}
				handleDataDetail={() => handleClick()} />
			{/* <MovieList
				title="Hành động"
				listMovie={props.Action}
				handleDataDetail={handleClick} />
			<MovieList
				title="Hài"
				listMovie={props.Comedy}
				handleDataDetail={handleClick} />
			<MovieList
				title="Kinh dị"
				listMovie={props.Horror}
				handleDataDetail={handleClick} />
			<MovieList
				title="Lãng mạn"
				listMovie={props.Romance}
				handleDataDetail={handleClick} />
			<MovieList
				title="Tài Liệu"
				listMovie={props.Document}
				handleDataDetail={handleClick} /> */}
			<DetailMovie
				title={props.titleTrailer}
				date_release={props.date_release}
				vote={props.vote}
				overview={props.overview}
				url={props.url}
				status={status} />
		</Card>
	);
}

export default Browse;
