import React from 'react';

import {
    Card,
    CardTitle,
    Button,
    CardText,
    CardImgOverlay,
    CardImg
}

    from 'reactstrap';
import './banner.scss'

const Banner = props => {
    return (<Card> <CardImg src={
        `https: //image.tmdb.org/t/p/original/${props.poster_path}`}></CardImg>

        <CardImgOverlay> <CardTitle> {
            props.name
        }

        </CardTitle> <CardTitle> <Button>Play</Button> <Button>My List</Button> </CardTitle> <CardText> {
            props.overview
        }

            </CardText> </CardImgOverlay> </Card>);
}

export default Banner;
