import React from 'react';
import { CardTitle, Row, Col, CardImg } from 'reactstrap';
import './movieList.scss'

const MovieList = props => {
    return (
        <Row>
            <Col xs="12">
                <CardTitle>{props.title}</CardTitle>
            </Col>
            {
                props.listMovie?.map((item, index) => (
                    <Col key={index}>
                        <CardImg style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original${item[0].poster_path})` }}></CardImg>
                    </Col>
                ))
            }
        </Row>
    );
}

export default MovieList;
