import React from 'react';
import {
	BrowserRouter,
	Routes,
	Route,
} from "react-router-dom";
import './App.css';
import Browse from './pages/browse/Browse';
import Navbar from './pages/component/navbar';
import Search from './pages/search/Search';



const requests = {
	fetchTrending: `trending/all/week?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&language=en-US`,
	fetchNetflixOriginals: `discover/tv?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&with_network=123`,
	fetchTopRated: `movie/top_rated?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&language=en-US`,
	fetchActionMovies: `discover/movie?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&with_genres=28`,
	fetchComedyMovies: `discover/movie?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&with_genres=35`,
	fetchHorrorMovies: `discover/movie?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&with_genres=27`,
	fetchRomanceMovies: `discover/movie?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&with_genres=10749`,
	fetchDocumentaries: `discover/movie?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&with_genres=99`,
	fetchSearch: `search/movie?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb&language=en-US`
};


function App() {
	const [loading, setloading] = React.useState(false);
	const [error, seterror] = React.useState(false);
	const [films, setfilms] = React.useState(false);
	const fetchHandleMovie = React.useCallback(
		async () => {
			try {
				// set Status loading when get data
				setloading(true);
				seterror(false);


				const movieBanner = await fetch(`https://api.themoviedb.org/3/movie/550?api_key=e2bc2cc2c5780ae2c6ffab88b2ba43fb`);
				const dataMovieBanner = await movieBanner.json();
				const movieOriginals = await fetch(`https://api.themoviedb.org/3/${requests.fetchNetflixOriginals}`);
				const dataMovieOriginals = await movieOriginals.json();
				const movieTrending = await fetch(`https://api.themoviedb.org/3/${requests.fetchTrending}`);
				const dataMovieTrending = await movieTrending.json();
				const movieToprated = await fetch(`https://api.themoviedb.org/3/${requests.fetchTopRated}`);
				const dataMovieToprated = await movieToprated.json();
				const movieAction = await fetch(`https://api.themoviedb.org/3/${requests.fetchActionMovies}`);
				const dataMovieAction = await movieAction.json();
				const movieComedy = await fetch(`https://api.themoviedb.org/3/${requests.fetchComedyMovies}`);
				const dataMovieComedy = await movieComedy.json();
				const movieHorror = await fetch(`https://api.themoviedb.org/3/${requests.fetchHorrorMovies}`);
				const dataMovieHorror = await movieHorror.json();
				const movieRomance = await fetch(`https://api.themoviedb.org/3/${requests.fetchRomanceMovies}`);
				const dataMovieRomance = await movieRomance.json();
				const movieDocument = await fetch(`https://api.themoviedb.org/3/${requests.fetchDocumentaries}`);
				const dataMovieDocument = await movieDocument.json();
				if (!movieBanner.ok) {
					throw new Error('Some thing error')
				}
			} catch (err) {
				seterror(err)
			}
		}
	)
	fetchHandleMovie();
	return (
		<BrowserRouter>
			<Navbar />
			<Routes>
				<Route path="/" element={<Browse />} />
				<Route path="/search" element={<Search />} />
			</Routes>
		</BrowserRouter>
	);
}

export default App;
