import React from 'react';
import { CardTitle, Row, Col, CardImg, Button } from 'reactstrap';
import './movieList.scss';
import Slider from "react-slick";

const setting = {
    dots: false,
    infinity: true,
    speed: 500,
    slidesToShow: 8,
    slidesToScroll: 1
}

const MovieList = props => {
    // const handleClick = (id, name, overview, vote_average, first_air_date, status) => {
    //     // props.handlePassData([id, name, overview, vote_average, first_air_date, status])
    //     props.handlePassData('ok')
    //     // console.log(id, name, overview, vote_average, first_air_date, status)
    // }
    return (
        <Row className="section-movieList">
            <Col xs="12">
                <CardTitle className="h2 title">{props.title}</CardTitle>
            </Col>
            <Slider {...setting}>
                {
                    props.listMovie?.map((item, index) => (
                        <Col key={index} className="box-image" lg="1" >
                            <Button onClick={props.handleDataDetail(item[0].id, item[0].name, item[0].overview, item[0].vote_average, item[0].first_air_date, true)}
                                style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original${item[0].poster_path})` }}>
                            </Button>
                        </Col>
                    ))
                }
            </Slider>
        </Row >
    );
}

export default MovieList;
